import React, { useContext } from 'react';
import StarWarsContext from '../context/StarWarsContext';

function PlanetsFilters() {
  const {
    data,
    handleFilterByName,
    handleInputColumn,
    handleInputComparison,
    handleInputValue,
    setFilteredData,
    filters: {
      filterByName: { name },
      filterByNumericValues: { column, comparison, value },
    } } = useContext(StarWarsContext);

  const dataFilteredByName = data.filter((planet) => planet.name.includes(name));
  setFilteredData(dataFilteredByName);
  const filterDataButton = () => {
    if (column && comparison && value) {
      switch (comparison) {
      case 'maior que':
        setFilteredData(dataFilteredByName.filter((planet) => (planet[column] > value)));
        break;
      case 'menor que':
        setFilteredData(dataFilteredByName.filter((planet) => (planet[column] < value)));
        break;
      case 'igual a':
        setFilteredData(dataFilteredByName
          .filter((planet) => (planet[column] === value)));
        break;
      default:
        setFilteredData(dataFilteredByName);
      }
    }
  };

  return (
    <div>
      <input
        type="text"
        data-testid="name-filter"
        onChange={ handleFilterByName }
        placeholder="Filter by Name"
      />
      <select
        data-testid="column-filter"
        onChange={ handleInputColumn }
      >
        <option value="population">population</option>
        <option value="orbital_period">orbital_period</option>
        <option value="diameter">diameter</option>
        <option value="rotation_period">rotation_period</option>
        <option value="surface_water">surface_water</option>
      </select>
      <select
        data-testid="comparison-filter"
        onChange={ handleInputComparison }
      >
        <option value="maior que">maior que</option>
        <option value="menor que">menor que</option>
        <option value="igual a">igual a</option>
      </select>
      <input
        type="number"
        placeholder="value"
        onChange={ handleInputValue }
      />
      <button
        type="button"
        data-testid="button-filter"
        onClick={ filterDataButton }
      >
        Fitler
      </button>
    </div>
  );
}

export default PlanetsFilters;
